import ReactDOM from "react-dom";
import React, { useState } from "react";

import Jumbotron from "react-bootstrap/Jumbotron";
import Toast from "react-bootstrap/Toast";
import Container from "react-bootstrap/Container";

import {
  Button,
  Nav,
  Navbar,
  NavDropdown,
  Form,
  FormControl
} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

export default class Welcome extends React.Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  async conponentDidMount() {}
  conponentDidUpdate() {}
  conponentWillUnmount() {}

  ExampleToast = ({ children }) => {
    const [show, toggleShow] = useState(true);
    return (
      <>
        {!show && <Button onClick={() => toggleShow(true)}>Show Toast</Button>}
        <Toast show={show} onClose={() => toggleShow(false)}>
          <Toast.Header>
            <strong className="mr-auto">React-Bootstrap</strong>
          </Toast.Header>
          <Toast.Body>{children}</Toast.Body>
        </Toast>
      </>
    );
  };

  render() {
    return (
      <Container className="p-3">
        <Navbar bg="light" expand="lg">
          <Navbar.Brand href="#home">Jonathan Williams</Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#link">Public Projects</Nav.Link>
              <Nav.Link href="#link">Private Projects</Nav.Link>
              <Nav.Link href="#link">Contact</Nav.Link>
            </Nav>
            <Form inline>
              <FormControl
                type="text"
                placeholder="Search"
                className="mr-sm-2"
              />
              <Button variant="outline-success">Search</Button>
            </Form>
          </Navbar.Collapse>
        </Navbar>
        <section class="page-section bg-primary text-white mb-0">
          <p>
            Hello..... Its me..... This is Home and there will be a round icon
            that is me
          </p>
        </section>
        <Jumbotron>
          <h1 className="header">Welcome To React-Bootstrap</h1>
          <ExampleToast className="toast">
            We now have Toasts
            <span role="img" aria-label="tada">
              ������
            </span>
          </ExampleToast>
        </Jumbotron>
      </Container>
    );
  }
}

const ExampleToast = ({ children }) => {
  const [show, toggleShow] = useState(true);

  return (
    <>
      {!show && <Button onClick={() => toggleShow(true)}>Show Toast</Button>}
      <Toast show={show} onClose={() => toggleShow(false)}>
        <Toast.Header>
          <strong className="mr-auto">React-Bootstrap</strong>
        </Toast.Header>
        <Toast.Body>{children}</Toast.Body>
      </Toast>
    </>
  );
};
